window.directBasketDataAfterChange = function () {
    let productList = [];

    for (productId in window.listProductsInBasket) {
        product = window.listProductsInBasket[productId];

        productList.push(
            {
                product: {
                    ids: {
                        userIdNikon: productId,
                    },
                    price: product.PRICE
                },
                count: product.COUNT,
                price: product.PRICE * product.COUNT
            }
        );
    }

    mindbox("async", {
        operation: "nikonstoreSetCartList",
        data: {
            productList
        },
    });
};

