                        "actionField": {"list": "Список товаров"},
                        "products": [{
                            "id": id,
                            "name": name,
                            "category": category,
                            "brand": brand,
                            "price": price,
                            "position": position
                        }]
                    }
                }
            });
            dataLayer.push({
                "event": "addToCart",
                "ecommerce": {
                    "add": {
                        "products": [{
                            "id": id,
                            "name": name,
                            "category": category,
                            "variant": variant,
                            "brand": brand,
                            "price": price,
                            "position": position,
                            "quantity": 1
